mapper.select:
select r.role_code as roleCode, r.role_cn_name as roleCnName, d.dept_name as deptName, c.city_name as cityName, u.* from sys_user u inner join ba_city c on c.id = u.city inner join sys_user_role ur on ur.user_id = u.id inner join sys_role r on r.id = ur.role_id inner join sys_dept d on d.id = u.dept_id where u.del_flag=#{userCo.delFlag}
and concat(u.real_name u.email) like concat('%',#{userCo.keyword},'%')
order by u.create_time desc

select
    su.id,
    su.real_name
from sys_user su, sys_user_role sur, sys_role sr
where
su.enabled = #{enabled}
and su.id = sur.user_id
and sur.role_id = sr.id
and su.dept_id = #{deptId}
and sr.role_code = #{amRoleCode}

select
    su.id,
    su.real_name
from sys_user su, sys_user_role sur, sys_role sr
where
su.enabled = #{enabled}
and su.id = sur.user_id
and sur.role_id = sr.id
and su.dept_id = #{deptId}
and sr.role_code = #{assistantRoleCode}
