mapper.select:
select
    su.id,
    su.real_name
from sys_user su, sys_user_role sur, sys_role sr
where
su.enabled = #{enabled}
and su.id = sur.user_id
and sur.role_id = sr.id
and su.dept_id = #{deptId}
and sr.role_code = #{amRoleCode}

